    address public constant CAMELOT_FACTORY =
        0x6EcCab422D763aC031210895C81787E87B43A652;

    ISwapRouter public immutable UNISWAP_V3_ROUTER_CONTRACT =
        ISwapRouter(UNISWAP_V3_ROUTER);

    // For this example, we will set the pool fee to 0.3%.
    uint24 public constant UNISWAP_V3_POOLFEE = 3000;

    IUniswapV2Router02 public immutable sushiwapRouter =
        IUniswapV2Router02(SUSHI_SWAP_ROUTER);

    ICamelotRouter public immutable camelotRouter =
        ICamelotRouter(CAMELOT_ROUTER);

    constructor() {
        // Approve on all 3 exchanges
        IERC20(WETH9).approve(address(UNISWAP_V3_ROUTER),type(uint256).max);
        IERC20(WETH9).approve(address(SUSHI_SWAP_ROUTER),type(uint256).max);
        IERC20(WETH9).approve(address(CAMELOT_ROUTER),type(uint256).max);

        IERC20(ARB).approve(address(UNISWAP_V3_ROUTER),type(uint256).max);
        IERC20(ARB).approve(address(SUSHI_SWAP_ROUTER),type(uint256).max);
        IERC20(ARB).approve(address(CAMELOT_ROUTER),type(uint256).max);

        IERC20(USDC).approve(address(UNISWAP_V3_ROUTER),type(uint256).max);
        IERC20(USDC).approve(address(SUSHI_SWAP_ROUTER),type(uint256).max);
        IERC20(USDC).approve(address(CAMELOT_ROUTER),type(uint256).max);
    }

    function performArbitrage(
        uint256 firstTradeExchange,
        uint256 secondTradeExchange,
        address tokenIn,
        uint256 amountIn
    ) external onlyOwner {
        uint256 preWeth9Balance = IERC20(WETH9).balanceOf(address(this));
        uint256 preUSDCBalance = IERC20(USDC).balanceOf(address(this));

        uint256 amountOut;
        if (firstTradeExchange == 0) {
            amountOut = swapTokensOnUniswapV3(tokenIn, ARB, amountIn, 1);
        } else if (firstTradeExchange == 1) {
            amountOut = swapTokensOnSushiswap(tokenIn, ARB, amountIn, 1);
        } else if (firstTradeExchange == 2) {
            amountOut = swapTokensOnCamelot(tokenIn, ARB, amountIn, 1);
        }

        if (secondTradeExchange == 0) {
            swapTokensOnUniswapV3(ARB, tokenIn, amountOut, amountIn);
        } else if (secondTradeExchange == 1) {
            swapTokensOnSushiswap(ARB, tokenIn, amountOut, amountIn);
        } else if (secondTradeExchange == 2) {
            swapTokensOnCamelot(ARB, tokenIn, amountOut, amountIn);
        }

        uint256 postWeth9Balance = IERC20(WETH9).balanceOf(address(this));
        uint256 postUSDCBalance = IERC20(USDC).balanceOf(address(this));

        require(postWeth9Balance >= preWeth9Balance, "Loss incurred on WETH9");
        require(postUSDCBalance >= preUSDCBalance, "Loss incurred on USDC");
    }

    function swapTokensOnUniswapV3(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) internal returns (uint256 amountOut) {
        // Naively set amountOutMinimum to 0. In production, use an oracle or other data source to choose a safer value for amountOutMinimum.
        // We also set the sqrtPriceLimitx96 to be 0 to ensure we swap our exact input amount.
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: _tokenIn,
                tokenOut: _tokenOut,
                fee: UNISWAP_V3_POOLFEE,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: _amountIn,
                amountOutMinimum: _amountOutMin,
                sqrtPriceLimitX96: 0
            });

        // The call to `exactInputSingle` executes the swap.
        amountOut = UNISWAP_V3_ROUTER_CONTRACT.exactInputSingle(params);
    }

    function swapTokensOnSushiswap(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) internal returns (uint256) {

        uint256 preTokenOutBalance = IERC20(_tokenOut).balanceOf(address(this));

        address[] memory path = new address[](2);
        path[0] = _tokenIn;
        path[1] = _tokenOut;

        sushiwapRouter.swapExactTokensForTokens(
            _amountIn,
            _amountOutMin,
            path,
            address(this),
            block.timestamp
        );

        uint256 postTokenOutBalance = IERC20(_tokenOut).balanceOf(
            address(this)
        );

        return postTokenOutBalance - preTokenOutBalance;
    }

    function swapTokensOnCamelot(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) internal returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = _tokenIn;
        path[1] = _tokenOut;

        uint256 preTokenOutBalance = IERC20(_tokenOut).balanceOf(address(this));

        camelotRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _amountIn,
            _amountOutMin,
            path,
            address(this),
            address(0),
            block.timestamp
        );

        uint256 postTokenOutBalance = IERC20(_tokenOut).balanceOf(
            address(this)
        );

        return postTokenOutBalance - preTokenOutBalance;
    }

    function withdraw() public onlyOwner {
        msg.sender.transfer(address(this).balance);
    }

    function withdrawToken(address _token) public onlyOwner {
        IERC20(_token).transfer(
            msg.sender,
            IERC20(_token).balanceOf(address(this))
        );
    }

    function getPriceFromUniswapV3(
        address tokenIn,
        address tokenOut
    ) public view returns (uint256 price) {
        IUniswapV3Factory factory = IUniswapV3Factory(UNISWAP_V3_FACTORY);
        IUniswapV3Pool pool = IUniswapV3Pool(
            factory.getPool(tokenIn, tokenOut, UNISWAP_V3_POOLFEE)
        );

        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();

        uint256 numerator1 = uint256(sqrtPriceX96) * uint256(sqrtPriceX96);
        uint256 numerator2 = 10 ** IERC20Metadata(pool.token0()).decimals();

        uint256 rawPrice = FullMath.mulDiv(numerator1, numerator2, 1 << 192);

        if (tokenIn == pool.token0()) {
            price = rawPrice;
        } else {
            price = FullMath.mulDiv(10 ** IERC20Metadata(pool.token0()).decimals(), 10 ** IERC20Metadata(pool.token1()).decimals(), rawPrice);
        }
    }

    function getPriceFromSushiswap(
        address tokenIn,
        address tokenOut
    ) public view returns (uint256) {
        IUniswapV2Pair pair = IUniswapV2Pair(
            IUniswapV2Factory(SUSHI_SWAP_FACTORY).getPair(tokenIn, tokenOut)
        );
        (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
        address token0 = pair.token0();

        if (tokenIn == token0) {
            return (reserve1 * (10 ** IERC20Metadata(tokenIn).decimals())) / reserve0;
        } else {
            return (reserve0 * (10 ** IERC20Metadata(tokenIn).decimals())) / reserve1;
        }
    }

    function getPriceFromCamelot(
        address tokenIn,
        address tokenOut
    ) public view returns (uint256) {
        IUniswapV2Pair pair = IUniswapV2Pair(
            IUniswapV2Factory(CAMELOT_FACTORY).getPair(tokenIn, tokenOut)
        );
        (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
        address token0 = pair.token0();

        if (tokenIn == token0) {
            return (reserve1 * (10 ** IERC20Metadata(tokenIn).decimals())) / reserve0;
        } else {
            return (reserve0 * (10 ** IERC20Metadata(tokenIn).decimals())) / reserve1;
        }
    }

    function getPriceOnAllExchanges(
        address tokenIn,
        address tokenOut
    ) public view returns (uint256, uint256, uint256) {
        return (
            getPriceFromUniswapV3(tokenIn, tokenOut),
            getPriceFromSushiswap(tokenIn, tokenOut),
            getPriceFromCamelot(tokenIn, tokenOut)
        );
    }
}
